"use client"

import { useState, useEffect, useContext } from "react"
import { AuthContext } from "../context/AuthContext"
import { API_URL } from "../config"
import TicketList from "../components/tickets/TicketList"
import CreateTicketForm from "../components/tickets/CreateTicketForm"
import { AlertCircle } from 'lucide-react'

const UserDashboard = () => {
  const { user, axiosInstance } = useContext(AuthContext)
  const [tickets, setTickets] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  // Fetch user's tickets
  useEffect(() => {
    const fetchTickets = async () => {
      try {
        setLoading(true)
        setError(null)

        console.log("Fetching tickets...")
        const response = await axiosInstance.get(`${API_URL}/api/tickets`)
        console.log("Tickets response:", response.data)

        setTickets(response.data)
      } catch (err) {
        console.error("Fetch tickets error:", err)
        setError(err.response?.data?.message || "Failed to fetch tickets")
      } finally {
        setLoading(false)
      }
    }

    if (user?.token) {
      fetchTickets()
    }
  }, [user, axiosInstance])

  // Add new ticket to the list
  const addTicket = (ticket) => {
    setTickets([ticket, ...tickets])
  }

  if (!user) {
    return (
      <div className="flex h-[400px] items-center justify-center">
        <div className="text-center">
          <AlertCircle className="mx-auto h-12 w-12 text-yellow-500" />
          <h2 className="mt-4 text-lg font-semibold text-gray-900 dark:text-white">
            Authentication Required
          </h2>
          <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
            Please log in to view your tickets
          </p>
        </div>
      </div>
    )
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-white">My Support Tickets</h1>
        <p className="mt-1 text-sm text-gray-600 dark:text-gray-400">
          View and manage your support tickets
        </p>
      </div>

      <div className="grid gap-8 md:grid-cols-[1fr_300px]">
        <div>
          {loading ? (
            <div className="flex h-40 items-center justify-center">
              <div className="flex items-center space-x-2">
                <div className="h-4 w-4 animate-spin rounded-full border-2 border-primary-500 border-t-transparent"></div>
                <span className="text-sm text-gray-500 dark:text-gray-400">Loading tickets...</span>
              </div>
            </div>
          ) : error ? (
            <div className="rounded-lg bg-red-50 p-4 dark:bg-red-900/30">
              <div className="flex items-center">
                <AlertCircle className="h-5 w-5 text-red-500 dark:text-red-400" />
                <p className="ml-2 text-sm text-red-700 dark:text-red-200">{error}</p>
              </div>
            </div>
          ) : (
            <TicketList tickets={tickets} />
          )}
        </div>
        <div>
          <CreateTicketForm addTicket={addTicket} />
        </div>
      </div>
    </div>
  )
}

export default UserDashboard
