"use client"

import { createContext, useState, useEffect, useCallback } from "react"
import axios from "axios"
import { API_URL } from "../config"

export const AuthContext = createContext()

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null)
  const [accessToken, setAccessToken] = useState(null)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  // Initialize axios instance with interceptors
  const axiosInstance = axios.create({
    baseURL: API_URL,
    withCredentials: true, // Required for cookies
  })

  // Add request interceptor
  axiosInstance.interceptors.request.use(
    (config) => {
      if (accessToken) {
        config.headers.Authorization = `Bearer ${accessToken}`
      }
      return config
    },
    (error) => {
      return Promise.reject(error)
    },
  )

  // Add response interceptor
  axiosInstance.interceptors.response.use(
    (response) => response,
    async (error) => {
      const originalRequest = error.config

      // If error is not 401 or request has already been retried, reject
      if (error.response?.status !== 401 || originalRequest._retry) {
        return Promise.reject(error)
      }

      originalRequest._retry = true

      try {
        // Try to refresh the token
        const response = await axios.post(`${API_URL}/api/auth/refresh-token`, {}, { withCredentials: true })

        const { accessToken: newAccessToken } = response.data.data

        setAccessToken(newAccessToken)

        // Retry the original request with new token
        originalRequest.headers.Authorization = `Bearer ${newAccessToken}`
        return axios(originalRequest)
      } catch (refreshError) {
        // If refresh fails, logout user
        logout()
        return Promise.reject(refreshError)
      }
    },
  )

  // Load user from storage and validate token
  useEffect(() => {
    const initializeAuth = async () => {
      const storedToken = localStorage.getItem("accessToken")
      if (storedToken) {
        setAccessToken(storedToken)
        try {
          const response = await axiosInstance.get("/api/auth/me")
          setUser(response.data.data.user)
        } catch (error) {
          localStorage.removeItem("accessToken")
          setAccessToken(null)
          setUser(null)
        }
      }
      setLoading(false)
    }

    initializeAuth()
  }, [axiosInstance])

  // Register user
  const register = async (userData) => {
    try {
      setError(null)
      const response = await axiosInstance.post("/api/auth/register", userData)
      const { user, accessToken } = response.data.data

      localStorage.setItem("accessToken", accessToken)
      setAccessToken(accessToken)
      setUser(user)

      return user
    } catch (error) {
      setError(error.response?.data?.message || "Registration failed")
      throw error
    }
  }

  // Login user
  const login = async (email, password) => {
    try {
      setError(null)
      const response = await axiosInstance.post("/api/auth/login", { email, password })
      const { user, accessToken } = response.data.data

      localStorage.setItem("accessToken", accessToken)
      setAccessToken(accessToken)
      setUser(user)

      return user
    } catch (error) {
      setError(error.response?.data?.message || "Login failed")
      throw error
    }
  }

  // Logout user
  const logout = useCallback(async () => {
    try {
      await axiosInstance.post("/api/auth/logout")
    } catch (error) {
      console.error("Logout error:", error)
    } finally {
      localStorage.removeItem("accessToken")
      setAccessToken(null)
      setUser(null)
    }
  }, [axiosInstance])

  // Update user profile
  const updateProfile = async (userData) => {
    try {
      const response = await axiosInstance.put("/api/auth/profile", userData)
      setUser(response.data.data.user)
      return response.data.data.user
    } catch (error) {
      setError(error.response?.data?.message || "Profile update failed")
      throw error
    }
  }

  return (
    <AuthContext.Provider
      value={{
        user,
        loading,
        error,
        register,
        login,
        logout,
        updateProfile,
        isAuthenticated: !!user,
        axiosInstance,
      }}
    >
      {children}
    </AuthContext.Provider>
  )
}
